import React, { Component } from 'react';
import Notification from './Notify/Notify';
import FeedBackOptions from './FeedBack/FeedBackOptions';
import Section from './Section/Section'
import Statistic from './Statistics/Statistic';

export class App extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0
  }

  onLeaveFeedback = (e) => {
    const count = e.target.name;
    this.setState(prevState => ({
      [count]: prevState[count] + 1
    }))
  };
  totalCount = () => {
    const { good, neutral, bad } = this.state;
    return good + neutral + bad;
  };
  positivePercent = () => {
    const { good, bad, neutral } = this.state
    let positivePercent = 0
    const finalPositivePercentage = Math.round((good / (bad + neutral + good)) * 100)
    if (finalPositivePercentage > 0) {
      positivePercent = finalPositivePercentage
    }
    return positivePercent;
  };

  render() {
    const { good, neutral, bad } = this.state;
    const totalFinds = this.totalCount();
    const positivePercent = this.positivePercent();
    const isFeedback = good || bad || neutral;

    return (
			<>
        <Section title="Please leave feedback">
          <FeedBackOptions options={Object.keys(this.state)} onLeaveFeedback={this.onLeaveFeedback} />
          {totalFinds === 0 ? (
            <Notification text="No feedback"/>
          ) : <Statistic
            good={good}
            neutral={neutral}
            bad={bad}
            total={totalFinds}
            positivePercent={positivePercent}
          />
          }


          </Section>
			</>
		);
  }
}
